import React, { Component } from 'react';
import './App.css';
import TaskForm from './components/TaskForm';
import Control from './components/Control';
import TaskList from './components/TaskList';
class App extends Component {

    constructor(props) {
        super(props)
        this.state = {
            tasks: [],
        }
    }

    onGenerateData = () => {
        var tasks = [
            {
                id: this.generRateId(),
                name: 'Learn Php Basic',
                status: true
            },
            {
                id: this.generRateId(),
                name: 'Learn Larvel Framework',
                status: true
            },
            {
                id: this.generRateId(),
                name: 'Learn React js',
                status: true
            }
        ];
        console.log(tasks);
    }

    s4() {
        return Math.floor((1+Math.random()) * 0x10000).toString(16).substring(1);
    }

    generRateId() {
        return this.s4() + this.s4() + '-' + this.s4() + '+' + this.s4() + '+' + this.s4() + '+' + this.s4() + '+' + this.s4() + '+' + this.s4();
    }

    render() {
        return (
            <div className="container">
                <div className="text-center">
                    <h1>Quản lý công việc</h1><hr/>
                </div>
                <div className="row">
                    <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
                        {/*Form*/}
                        <TaskForm />
                    </div>                   
                    <div className="col-xs-8 col-sm-8 col-md-8 col-lg-8">
                        <button type="button" className="btn btn-primary"><span className="fa fa-plus mr-5"></span>Thêm công việc</button>&nbsp;
                        <button type="button" className="btn btn-danger" onClick={ this.onGenerateData }>Generate Data</button>
                        <br/>
                        <br/>
                        {/* Search - Sort */}
                            <Control />
                        <div className="row mt-15">
                            <TaskList />
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
